import DetectionUtil from './detection';

describe('isBiDirectional()', () => {
  const paragraph = document.createElement('p');

  it('should return true if the dir attribute is set to rtl', () => {
    document.documentElement.setAttribute('dir', 'rtl');
    expect(DetectionUtil.isBiDirectional()).toEqual(true);

    paragraph.setAttribute('dir', 'rtl');
    expect(DetectionUtil.isBiDirectional(paragraph)).toEqual(true);
  });

  it('should return false if there is no dir attribute', () => {
    document.documentElement.removeAttribute('dir');
    expect(DetectionUtil.isBiDirectional()).toEqual(false);

    paragraph.removeAttribute('dir');
    expect(DetectionUtil.isBiDirectional(paragraph)).toEqual(false);
  });

  it('should return false if the dir attribute is set to ltr', () => {
    document.documentElement.setAttribute('dir', 'ltr');
    expect(DetectionUtil.isBiDirectional()).toEqual(false);

    paragraph.setAttribute('dir', 'ltr');
    expect(DetectionUtil.isBiDirectional(paragraph)).toEqual(false);
  });
});
